export default `body{margin:0;font-family:Arial,sans-serif;scroll-behavior:smooth}.navbar>img.logo-img{width:400px;height:60px}.navbar{display:flex;justify-content:space-between;align-items:center;background:#222;color:#fff;padding:15px 30px;flex-wrap:wrap}.navbar .logo{font-weight:700;font-size:1.2rem}.navbar ul{list-style:none;display:flex;gap:20px;margin:0;padding:0}.navbar ul li a{color:#fff;text-decoration:none;transition:color .3s}.navbar ul li a:hover{color:#ff9800}.hero{color:#fff;text-align:center;padding:120px 20px}.hero h1{font-size:2.5rem}.hero p{font-size:1.2rem;margin:20px 0}.hero .btn{background:#ff9800;padding:12px 24px;color:#fff;text-decoration:none;border-radius:5px;display:inline-block;transition:background .3s}.hero .btn:hover{background:#e68900}.section{padding:60px 20px;text-align:center}.section h2{font-size:2rem;margin-bottom:40px}.grid{display:flex;gap:20px;justify-content:center;flex-wrap:wrap}.card{background:#f4f4f4;padding:20px;border-radius:10px;width:300px;transition:transform .3s}.card:hover{transform:translateY(-5px)}.card img{width:100%;border-radius:10px}.contacto form{display:flex;flex-direction:column;gap:10px;max-width:400px;margin:auto}.contacto form input,.contacto form textarea{padding:10px;border:1px solid #ddd;border-radius:5px;font-size:1rem}.contacto form button{background:#222;color:#fff;padding:12px;border:none;cursor:pointer;font-size:1rem;border-radius:5px;transition:background .3s}.contacto form button:hover{background:#444}.footer{background:#222;color:#fff;text-align:center;padding:15px;font-size:.9rem}@media (max-width: 1024px){.hero h1{font-size:2rem}.hero p{font-size:1rem}.navbar ul{gap:15px}}@media (max-width: 768px){.navbar{flex-direction:column;align-items:flex-start}.navbar ul{flex-direction:column;gap:10px;width:100%;margin-top:10px}.hero{padding:80px 20px}.hero h1{font-size:1.8rem}.hero p{font-size:1rem}.card{width:100%;max-width:90%}}@media (max-width: 480px){.hero{padding:60px 15px}.hero h1{font-size:1.5rem}.hero p{font-size:.9rem}.hero .btn{padding:10px 18px;font-size:.9rem}.section h2{font-size:1.5rem}.footer{font-size:.8rem}}
`;